function statMois($statistiques){
    $tabmois = [];

    foreach ($statistiques as $value) {
        $date = $value['date'];
        $date_parse = date_parse($date);
        $mois= $date_parse['month'];

        if (array_key_exists($mois, $tabmois)){
            $tabmois[$mois]++;
        }
        else
            $tabmois[$mois] = 1;
       

    }
    return $tabmois;
}

